mapper.select:
select *
from teacher
where T_ID = #{id} and T_PASSWORD = #{password}

mapper.update:
update teacher
T_SEX=#{message}, OFFICE=#{message}, T_BIRTHDAY = #{message}, PHONE = #{message}, POSITION = #{message}, LAB = #{message}, LIMIT = #{message}, AWARD = #{message}, TOPIC = #{message}, TEACHING_DIRECTION = #{message}, RESEARCH_AREA = #{message}, DEGREE = #{message}, GRADUATE_SCHOOL = #{message}, GRADUATE_TIME = #{message}, WORK_DATE = #{message}
where T_ID = #{id}
